mapper.select:
select

from c_order
where 1=1

      and taskid = #{taskid,jdbcType=INTEGER}


      and orderuserid = #{orderuserid,jdbcType=VARCHAR}


      and status = #{status,jdbcType=INTEGER}


      and createtime = #{createtime,jdbcType=VARCHAR}


      and overtime = #{overtime,jdbcType=VARCHAR}


      and id = #{id,jdbcType=INTEGER}

mapper.insert:
insert into c_order (id, taskid, orderuserid, 
  status, createtime, overtime
  )
values (#{id,jdbcType=INTEGER}, #{taskid,jdbcType=INTEGER}, #{orderuserid,jdbcType=VARCHAR}, 
  #{status,jdbcType=INTEGER}, #{createtime,jdbcType=VARCHAR}, #{overtime,jdbcType=VARCHAR}
  )
