mapper.update:
UPDATE c_order
taskid= #{order.taskid,jdbcType=VARCHAR}, orderuserid= #{order.orderuserid,jdbcType=VARCHAR}, status= #{order.status,jdbcType=INTEGER}, createtime= #{order.createtime,jdbcType=VARCHAR}, overtime= #{order.overtime,jdbcType=VARCHAR}
WHERE id = #{order.id,jdbcType=VARCHAR}

mapper.select:
select

from c_order
where 1=1

    and taskid = #{taskid,jdbcType=VARCHAR}


    and orderuserid = #{orderuserid,jdbcType=VARCHAR}


    and status = #{status,jdbcType=INTEGER}


    and createtime = #{createtime,jdbcType=VARCHAR}


    and overtime = #{overtime,jdbcType=VARCHAR}


    and id = #{id,jdbcType=VARCHAR}
