mapper.select:
select

from c_product
where 1=1

  and productname like #{productname,jdbcType=VARCHAR}


  and classid = #{classid,jdbcType=INTEGER}


  and userid = #{userid,jdbcType=VARCHAR}


  and content = #{content,jdbcType=VARCHAR}


  and imageids = #{imageids,jdbcType=VARCHAR}


  and createtime = #{createtime,jdbcType=VARCHAR}


  and id = #{id,jdbcType=INTEGER}
