mapper.select:
select

,

from c_task
where 1=1

  and title = #{title,jdbcType=VARCHAR}


  and userid = #{userid,jdbcType=VARCHAR}


  and status = #{status,jdbcType=VARCHAR}


  and createtime = #{createtime,jdbcType=VARCHAR}


  and content = #{content,jdbcType=LONGVARCHAR}


  and id = #{id,jdbcType=INTEGER}

mapper.insert:
insert into c_task (id, title, userid, 
  status, createtime, content
  )
values (#{id,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, #{userid,jdbcType=VARCHAR}, 
  #{status,jdbcType=VARCHAR}, #{createtime,jdbcType=VARCHAR}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.update:
update c_task
set title = #{title,jdbcType=VARCHAR},
  userid = #{userid,jdbcType=VARCHAR},
  status = #{status,jdbcType=VARCHAR},
  createtime = #{createtime,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
